def apartment_progress_bar(status: Dict[str, bool]) -> str:
    # ÑÑ‡Ð¸Ñ‚Ð°ÐµÐ¼ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ ÑƒÑ€Ð¾Ð²Ð½Ð¸ 5 Ð¸ 6 (Ñ€ÐµÐ¼Ð¾Ð½Ñ‚ + ÑÐ´Ð°Ñ‡Ð°)
    total = 0
    done = 0
    for q in MAIN_QUESTS.values():
        if q.level in (5, 6):
            total += 1
            if status.get(q.id):
                done += 1
    if total == 0:
        return "ðŸ¡ Ð£Ñ€Ð¾Ð²ÐµÐ½ÑŒ Ð°Ð¿Ð°Ñ€Ñ‚Ð°Ð¼ÐµÐ½Ñ‚Ð¾Ð²: [----------] 0/0"
    ratio = done / total
    steps = 10
    filled = int(round(ratio * steps))
    bar = "â–ˆ" * filled + "â–‘" * (steps - filled)
    return f"ðŸ¡ Ð£Ñ€Ð¾Ð²ÐµÐ½ÑŒ Ð°Ð¿Ð°Ñ€Ñ‚Ð°Ð¼ÐµÐ½Ñ‚Ð¾Ð²: [{bar}] {done}/{total}"


