async def lootbox_open(update: Update, context: ContextTypes.DEFAULT_TYPE):
    query = update.callback_query
    user = update.effective_user
    _, box_str = query.data.split(":", 1)
    box_type = int(box_str)

    ensure_user(user.id)
    coins = get_user_coins(user.id)
    cost = LOOTBOX_COSTS[box_type]
    if coins < cost:
        await query.answer("ÐÐµÐ´Ð¾ÑÑ‚Ð°Ñ‚Ð¾Ñ‡Ð½Ð¾ Ð¼Ð¾Ð½ÐµÑ‚ ðŸ˜¢")
        await query.edit_message_text(
            f"Ð£ Ñ‚ÐµÐ±Ñ Ð²ÑÐµÐ³Ð¾ {coins} Ð¼Ð¾Ð½ÐµÑ‚, Ð° Ð½ÑƒÐ¶Ð½Ð¾ {cost}.", reply_markup=MAIN_MENU_KB, parse_mode="Markdown"
        )
        return

    add_coins(user.id, -cost)

    table = LOOTBOX_REWARD_TABLES[box_type]
    roll = random.randint(1, 100)
    reward_text = table[roll - 1]

    if box_type == 1:
        rarity = "Common"
    elif box_type == 2:
        rarity = "Uncommon"
    elif box_type == 3:
        rarity = "Rare"
    elif box_type == 4:
        rarity = "Epic"
    else:
        rarity = "Legendary"

    add_reward_record(user.id, source=f"lootbox:{box_type}", rarity=rarity,
                      lootbox_type=box_type, reward_text=reward_text)

    text = lootbox_open_animation(box_type, reward_text)

    if MINI_EVENTS and random.random() < 0.25:
        ev = random.choice(MINI_EVENTS)
        text += f"\n\nðŸŽ² *ÐœÐ¸Ð½Ð¸-Ð¸Ð²ÐµÐ½Ñ‚ Ð´Ð½Ñ:* {ev['title']}\n{ev['text']}"

    partner_indices = PARTNER_REWARD_INDEXES.get(box_type, [])
    if PARTNER_USER_ID and (roll in partner_indices):
        try:
            await context.bot.send_message(
                chat_id=PARTNER_USER_ID,
                text=f"ðŸ’Œ Ð’Ð¸ Ð²Ñ‹Ð±Ð¸Ð»Ð° Ð½Ð°Ð³Ñ€Ð°Ð´Ñƒ, ÑÐ²ÑÐ·Ð°Ð½Ð½ÑƒÑŽ Ñ Ñ‚Ð¾Ð±Ð¾Ð¹ (d100={roll}):\n\n{reward_text}",
            )
        except Exception as e:
            logger.warning("ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ðµ Ð¿Ð°Ñ€Ñ‚Ð½Ñ‘Ñ€Ñƒ: %s", e)

